'use strict';

// system
const path = require('path');

// modules
const transform = require('./lib/transform');

module.exports = {
  transforms: [

    transform(file => {
      if (!file.path.split('/').some(name => name[0] === '_')) {
        return file;
      }
    }),

    transform('.js', file => {
      return file;
    }),
  ]
};
